mapper.select:
    select DISTINCT a.bus_id,b.bus_name,b.create_user_id,b.create_user_name,b.create_date,b.customer_name,b.customer_mobile,b.flow_id,b.status
    from bus_milestone a
    LEFT JOIN bus_task b on a.bus_id = b.bus_id
	where b.status = #{status}

      and a.assigement_user = #{assignUser}

	order BY b.create_date desc
